import React from 'react'
import './card.css'


export const BookCardWantToRead = (props) => {

  const deleteBook = (bookId) => {
    fetch(`http://localhost:8080/books/${bookId}`, {
      method: 'DELETE',
      headers: { 'Content-Type': 'application/json' },
    })
    .then((res) => {
      if (!res.ok) {
        throw new Error('Could not delete entry.');
      }
      return res.json();
    })
    .then((json) => {
      // console logs a message saying the entry was deleted successfully
      console.log(json);
    })
  }

  return (
    <div className="card">
      <div className="card-thumbnail" style={{
        backgroundImage: `url(${props.thumbnail})`,
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat'
      }}>
      </div>
      <div className="card-info">
        <div className="card-info-top">
          <h3 className="h3-title">{props.title}</h3>
          <p className="p-author">{props.author}</p>
        </div>

        <div className="card-info-bottom">
          <button>Read</button>
          <button onClick={() => deleteBook(props.key)}>Remove</button>
        </div>
      </div>
      <div className="card-actions">
        <p>◆</p>
      </div>
    </div >
  )
}


