import React, { useEffect, useState } from 'react'
import { bookCardWantToRead } from './bookCardWantToRead'

export const WantToRead = () => {
  const BOOKS_URL = "http://localhost:8080/books"
  const [books, setBooks] = useState([])

  useEffect(() => {
    fetchBooks()
  }, [])

  const fetchBooks = () => {
    // do a fetch to the local database
    fetch(BOOKS_URL)
      .then((res) => {
        // convert the resonse into json format
        console.log(res)
        return res.json()
      })
      .then((data) => {
        // NEED TO FILTER ON BOOKS WITH THE ISREAD=FALSE FLAG HERE
        const filteredBooks = data.filter(book => book.title)
        setBooks(filteredBooks)
        console.log("filteredBooks")
        console.log(filteredBooks)
      })
  }

  return (
    <div className="debug grow">
      <p>Want to read</p>
      <section className="card-list">
        {
          books.map(book => (
            <bookCardWantToRead
              author={book.author}
              title={book.title}
              googleId={book.googleId}
              isRead={book.isRead}
              thumbnail={book.thumbnail}
              key={book.googleId}
            />
          ))
        }

      </section>
    </div>
  )
}

