import React from 'react'
import './card.css'

export const BookCardSearch = (props) => {

  const addBook = (title) => {

    fetch(BOOKS_POST_URL, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ title: title })
    })
      .then(() => {
        // When the message is done being posted, fetch the messages again, so the new post is visible.
        fetchMessages()
      })
  }

  // title: String,
  // author: String,
  // thumbnail: String,
  // googleId: String,
  // isRead: {
  //   type: Boolean,
  //   default: false,
  // },
  // dateRead: {
  //   type: Date,
  //   default: () => new Date()
  // },
  // rating: {
  //   type: Number,
  //   default: null
  // },

  return (
    <div className="card">
      <div className="card-thumbnail" style={{
        backgroundImage: `url(${props.thumbnail})`,
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat'
      }}>
      </div>
      <div className="card-info">
        <div className="card-info-top">
          <h3 className="h3-title">{props.title}</h3>
          <p className="p-author">{props.author}</p>
        </div>
        <div className="card-info-bottom">
          <button>Add</button>
        </div>
      </div>
      <div className="card-actions">
        <p>◆</p>
      </div>
    </div >
  )
}
