import React, { useEffect, useState } from 'react';
export const NewMemberForm = ({ fetchMembers }) => {
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [isPapa, setIsPapa] = useState(false);

  const onFormSubmit = async (e) => {
    e.preventDefault();
    await fetch(`http://localhost:8080/books`, {
      method: 'POST',
      body: JSON.stringify({
        author: author,
        title: title,
        isPapa,
      }),
    });
    fetchMembers();
  };

  return (
    <form>
      <h2>New Book:</h2>

      <section>
        <label for="author">Author:</label>
        <input
          type="text"
          name="author"
          id="author"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        />
      </section>
      <section>
        <label for="title">Last Name:</label>
        <input
          type="text"
          name="title"
          id="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </section>
      <section>
        <label for="isPapa">Is Papa:</label>
        <input
          type="checkbox"
          name="isPapa"
          id="isPapa"
          value={isPapa}
          onChange={(e) => setIsPapa(e.target.value)}
        />
      </section>
      <button onClick={onFormSubmit}>Create</button>
    </form>
  );
};
