import React from 'react'
import './card.css'


export const BookCardWantToRead = (props) => {

  const deleteBook = (_id) => {
    fetch(`http://localhost:8080/books/${_id}`, {
      method: 'DELETE'
      // headers: { 'Content-Type': 'application/json' },
    })
      .then(res =>
        res.json().then(json => {
          return json;
        })
      );
  }

  return (
    <div className="card">
      <div className="card-thumbnail" style={{
        backgroundImage: `url(${props.thumbnail})`,
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat'
      }}>
      </div>
      <div className="card-info">
        <div className="card-info-top">
          <h3 className="h3-title">{props.title}</h3>
          <p className="p-author">{props.author}</p>
        </div>

        <div className="card-info-bottom">
          <button>Read</button>
          <button onClick={() => deleteBook(props._id)}>Remove</button>
        </div>
      </div>
      <div className="card-actions">
        <p>◆</p>
      </div>
    </div >
  )
}


